import { Component } from '@angular/core';
import particlesJS from 'particles.js';

@Component({
  selector: 'home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  particleConfig: { particles: { number: { value: number; }; color: { value: string; }; shape: { type: string; }; }; };

  constructor() {

    this.particleConfig = {
      particles: {
        number: {
          value: 100,
        },
        color: {
          value: '#000000',
        },
        shape: {
          type: 'circle',
        },
      },
    };
  }

  ngOnInit() {
    particlesJS('particles-js', this.particleConfig); // 'particles-js' è l'ID del tuo elemento HTML in cui vuoi renderizzare le particelle.
  }
}
